app.controller("ES6Controller",function(){
    //console.log(123);
    function log(x, y = 'World') {
        console.log(x, y);
    }
    log('Hello') // Hello World
    log('Hello', 'China') // Hello China
    log('Hello', '') // Hello

    function test(...values){
        console.log(arguments);
        console.log(values);
        var sum = 0;
        for (var val of values) {
            sum += val;
        }
        console.log(sum);
    }
    test(1,2,3);
})
