app.controller("canvasController",function(){
    var rect=document.getElementById("rect");
    var rectEle=rect.getContext("2d");
    rectEle.fillStyle="#FF0000";
    rectEle.fillRect(0,0,150,75);

    var tianchong = document.getElementById("tianchong");
    var tianchongEle = tianchong.getContext("2d");
    var grd = tianchongEle.createLinearGradient(0,0,170,0);
    grd.addColorStop(0,"black");
    grd.addColorStop(1,"white");
    tianchongEle.fillStyle=grd;
    tianchongEle.fillRect(20,20,150,100);

    var tianchong1 = document.getElementById("tianchong1");
    var tianchong1Ele = tianchong1.getContext("2d");
    var grd=tianchong1Ele.createRadialGradient(75,50,5,90,60,100);
    grd.addColorStop(0,"red");
    grd.addColorStop(1,"white");
    tianchong1Ele.fillStyle=grd;
    tianchong1Ele.fillRect(20,20,150,100);

    var shadowPosition=document.getElementById("myCanvas1");
    var shadowPositionEle=shadowPosition.getContext("2d");
    shadowPositionEle.shadowBlur=10;
    shadowPositionEle.shadowOffsetX=10;
    shadowPositionEle.shadowOffsetY=10;
    shadowPositionEle.shadowColor="black";
    shadowPositionEle.fillStyle="red";
    shadowPositionEle.fillRect(20,20,100,50);
})
