//原生js插件封装
(function($){

    //弹窗组件
    var layer;


    //tab选项卡插件
    var Tab = function(config){
        var that = this;
        //默认配制参数

        this.config = {
            eventType:"click",  //定义事件类型
            animate:"fade",  //定义动画效果
            invoke:"1",  //默认展示第几个
            auto:"5000"  //定义动画时间
        }

        this.getConfig(config);
        console.log()
        //绑定方法
        if(this.eventType == "click"){
            console.log("click");
            $(".tab-nav li").bind("click",function(){

            })
        }else if(this.eventType == "mouseover"){
            console.log("mouseover");
            $(".tab-nav li").bind("mouseover",function(){

            })
        }

    };
    Tab.prototype = {
        //获取配制参数信息
        getConfig:function(config){
            if(config && config !=""){
                $.extend(this.config,config)  //jquery的$.extend扩展方法，把第二个参数中的属性和值往第一个参数中扩展，有就覆盖，没有就添加.
            }else{

            }
        },



    };
    window.Tab = Tab;  //暴露出去

})(jQuery);

var tab1 = new Tab({
    eventType:"click",
    animate:"fade",
    auto:"3000",
    name:"luoliang"
});