app.controller("pluginController", function ($scope) {
    var tab1 = new Tab({
        eventType:"click",
        animate:"fade",
        invoke:"1",
        auto:"1"
    },"#tabTest");

    $scope.tipsLayer = function () {
        var layer1 = new Layer({
            layerType:"tips",
            content:"tips测试"
        })
    };

    $scope.alertLayer = function(){
        var layer2 = new Layer({
            layerType:"alert",
            width:"860",
            height:"560",
            title:"alert弹窗",
            isBtn:true,
            content:"alert测试",
            closeBtn:function(){
                alert("您点击了关闭按钮")
            },
            sureBtn:function(){
                alert("您点击了确定按钮")
            },
            falseBtn:function(){
                alert("您点击了取消按钮")
            }
        })
    }

    $scope.iframeLayer = function(){
        var layer3 = new Layer({
            layerType:"alert",
            width:"860",
            height:"560",
            title:"iframe加载窗",
            url:"http://www.baidu.com",
            isBtn:true,
            closeBtn:function(){   //监听关闭方法，也可以使用下面的自定义事件，效果一样,不过可以将方法单独提取出来，也是种不错的选择
                alert("我要关闭了")
            }
        });

        //自定义事件----监听弹窗关闭事件
        layer3.on("close",function(){
            alert(123);
        });
        //自定义事件----监听弹窗"确定"按钮事件
        layer3.on("sure",function(){
            alert(456);
        });
        //自定义事件----监听弹窗"取消"按钮事件
        layer3.on("false",function(){
            alert(789);
        })
    }
})
