app.controller("pluginController", function ($scope) {
    var tab1 = new Tab({
        eventType:"click",
        animate:"fade",
        invoke:"1",
        auto:"1"
    },"#tabTest");

    $scope.tipsLayer = function () {
        var layer1 = new Layer({
            layerType:"tips",
            content:"tips测试"
        })
    };

    $scope.alertLayer = function(){
        var layer2 = new Layer({
            layerType:"alert",
            width:"860",
            height:"560",
            title:"alert弹窗",
            isBtn:true,
            content:"alert测试",
            //closeBtn:function(){
            //    alert("点击测试")
            //},
            //sureBtn:function(){
            //    alert("确定按钮")
            //}
        })
    }

    $scope.iframeLayer = function(){
        var layer3 = new Layer({
            layerType:"alert",
            width:"860",
            height:"560",
            title:"iframe加载窗",
            url:"http://www.baidu.com",
            isBtn:true,
            closeBtn:function(){
                alert("我要关闭了")
            }
        });

        //自定义事件----监听弹窗关闭事件
        layer3.on("close",function(){
            alert(123);
        })
        layer3.on("sure",function(){
            alert(456);
        })
    }
})
