app.controller("window-objectController",function($scope){
    $scope.fullScreen = function(){
        //全屏
        var de = document.documentElement;
        if (de.requestFullscreen) {
            de.requestFullscreen();
        } else if (de.mozRequestFullScreen) {
            de.mozRequestFullScreen();
        } else if (de.webkitRequestFullScreen) {
            de.webkitRequestFullScreen();
        }
    };
    $scope.outFullScreen = function(){
        //退出全屏
        if (document.exitFullscreen) {
            document.exitFullscreen();
        }
        else if (document.webkitCancelFullScreen) {
            document.webkitCancelFullScreen();
        }
        else if (document.mozCancelFullScreen) {
            document.mozCancelFullScreen();
        }
    };

    $scope.pcMobile = function(){
        console.log(navigator.userAgent);
        if ((navigator.userAgent.match(/(phone|pad|pod|iPhone|iPod|ios|iPad|Android|Mobile|BlackBerry|IEMobile|MQQBrowser|JUC|Fennec|wOSBrowser|BrowserNG|WebOS|Symbian|Windows Phone)/i))) {
            // window.location.href="你的手机版地址";
            alert("移动浏览器");
        }else {
            // window.location.href="你的电脑版地址";
            alert("pc浏览器");
        }
    }

    $scope.explorer = function(){
        // 通过浏览器的navigator中的userAgent属性判断
        var explorer = navigator.userAgent;
        alert(explorer);
        //ie
        if (explorer.indexOf("MSIE") >= 0) {
            alert("IE");
            var isIE=!!window.ActiveXObject;
            var isIE6=isIE&&!window.XMLHttpRequest;
            var isIE8=isIE&&!!document.documentMode;
            var isIE7=isIE&&!isIE6&&!isIE8;

            // 如果是IE8及以下时，就返还给用户另一个网页，网页内容让用户更新浏览器或者更换浏览器
            if (isIE6){
                alert("ie6");
                window.open("http://www.baidu.com","_self");
            }else if (isIE8){
                alert("ie8");
            }else if (isIE7){
                alert("ie7");
            }

        }
        //firefox
        else if (explorer.indexOf("Firefox") >= 0) {
            alert("Firefox");
        }
        //Chrome和Safari都同用webkit的内核
        else if(explorer.indexOf("Chrome") >= 0){
            alert("Chrome");
        }
        //Opera
        else if(explorer.indexOf("Opera") >= 0){
            alert("Opera");
        }
        //Safari和Chrome都同用webkit的内核
        else if(explorer.indexOf("Safari") >= 0){
            alert("Safari");
        }
    }
})