var app = angular.module("myApp", ["ui.router"]);

app.config(function ($stateProvider, $urlRouterProvider) {

    //初始路径
    $urlRouterProvider.when("", "/htmlContent");
    $stateProvider
        //html内容部分
        .state("htmlContent", {
            url: "/htmlContent",
            views:{
                "":{
                    templateUrl:"./htmlContent/html.html"  //配制二级视图
                },
                "htmlContentTitle@htmlContent":{    //配制二级菜单的标题栏
                    templateUrl:"./htmlContent/htmlContentTitle/htmlContentTitle.html"
                },
                "htmlContentText@htmlContent":{     //二级子页面嵌套初始加载路径(htmlContentText.html)
                    templateUrl:"./htmlContent/htmlContentText/htmlContentText.html"
                }
            }
        })
        .state("htmlContent.input",{
            url:"/input",
            views:{
                "htmlContentText@htmlContent":{
                    templateUrl:"./htmlContent/htmlContentText/input/input.html"
                }
            }
        })
        .state("htmlContent.iframe",{
            url:"/iframe",
            views:{
                "htmlContentText@htmlContent":{
                    templateUrl:"./htmlContent/htmlContentText/iframe/iframe.html"
                }
            }
        })

        //css内容部分
        .state("cssContent", {
            url:"/cssContent",
            //templateUrl: "./cssContent/css.html"
            views:{
                "":{
                    templateUrl:"./cssContent/css.html"
                },
                "cssContentTitle@cssContent":{
                    templateUrl:"./cssContent/cssContentTitle/cssContentTitle.html"
                },
                "cssContentText@cssContent":{
                    templateUrl:"./cssContent/cssContentText/cssContentText.html"
                }
            }
        })
        .state("cssContent.layout",{
            url:"/layout",
            views:{
                "cssContentText@cssContent":{
                    templateUrl:"./cssContent/cssContentText/cssLayout/cssLayout.html"
                }
            }
        })
        .state("cssContent.cssImages",{
            url:"/cssImages",
            views:{
                "cssContentText@cssContent":{
                    templateUrl:"./cssContent/cssContentText/cssImages/cssImages.html"
                }
            }
        })
        .state("cssContent.cssCenter",{
            url:"/cssCenter",
            views:{
                "cssContentText@cssContent":{
                    templateUrl:"./cssContent/cssContentText/cssCenter/cssCenter.html"
                }
            }
        })
        .state("cssContent.cssIcon",{
            url:"/cssIcon",
            views:{
                "cssContentText@cssContent":{
                    templateUrl:"./cssContent/cssContentText/cssIcon/cssIcon.html"
                }
            }
        })

        //js内容部分
        .state("jsContent", {
            url:"/jsContent",
            views:{
                "":{
                    templateUrl:"./jsContent/js.html"
                },
                "jsContentTitle@jsContent":{
                    templateUrl:"./jsContent/jsContentTitle/jsContentTitle.html"
                },
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/jsContentText.html"
                }
            }
        })
        .state("jsContent.canvas",{
            url:"/canvas",
            views:{
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/canvas/canvas.html",
                    controller:"canvasController"
                }
            }
        })
        .state("jsContent.math",{
            url:"/math",
            views:{
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/math/math.html"
                }
            }
        })
        .state("jsContent.size",{
            url:"/size",
            views:{
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/size/size.html"
                }
            }
        })
        .state("jsContent.closure",{
            url:"/closure",
            views:{
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/closure/closure.html"
                }
            }
        })
        .state("jsContent.save",{
            url:"/save",
            views:{
                "jsContentText@jsContent":{
                    templateUrl:"./jsContent/jsContentText/save/save.html"
                }
            }
        })

        //工具部分
        .state("tipsContent", {
            url:"/tipsContent",
            views:{
                "":{
                    templateUrl:"./tipsContent/tips.html"
                },
                "tipsContentTitle@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentTitle/tipsContentTitle.html"
                },
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/tipsContentText.html"
                }
            }
        })
        .state("tipsContent.cmd",{
            url:"/cmd",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/cmd/cmd.html"
                }
            }
        })
        .state("tipsContent.ajax",{
            url:"/ajax",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/ajax/ajax.html"
                }
            }
        })
        .state("tipsContent.angular",{
            url:"/angular",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/angular/angular.html"
                }
            }
        })
        .state("tipsContent.weixin",{
            url:"/weixin",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/weixin/weixin.html"
                }
            }
        })
        .state("tipsContent.bootstrap",{
            url:"/bootstrap",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/bootstrap/bootstrap.html"
                }
            }
        })
        .state("tipsContent.easyui",{
            url:"/easyui",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/easyui/easyui.html"
                }
            }
        })
        .state("tipsContent.visual",{
            url:"/visual",
            views:{
                "tipsContentText@tipsContent":{
                    templateUrl:"./tipsContent/tipsContentText/visual/visual.html"
                }
            }
        })

});

